mapper.select:
SELECT id, date, username, hours, memo, remarks
FROM schedule
WHERE YEAR(date) = #{year}
  AND MONTH(date) = #{month}
ORDER BY date ASC, username ASC

SELECT id, date, username, hours, memo, remarks
FROM schedule
WHERE date = #{date}
  AND username = #{username}
LIMIT 1

mapper.update:
UPDATE schedule
SET hours   = #{hours},
    remarks = #{remarks}
WHERE id = #{id}

mapper.delete:
DELETE FROM schedule
WHERE date = #{date}
  AND username = #{username}

mapper.update:
UPDATE schedule
SET remarks = #{remarks}
WHERE date = #{date}
